select 
,U.REAL_NAME creatorName ,P.PARTNER_ADDRESS partnerAddress
from YDZ_SALESCHECKING_ORDER SCO
JOIN YDZ_USER U ON U.USER_KEY = SCO.CREATOR
JOIN YDZ_PARTNER P ON SCO.PARTNER_KEY = P.PARTNER_KEY
where SCO.IS_VALID = '1' AND SCO.IS_DEL = '0' and SCO.SALESCHECKING_NO = #{salescheckingNo}

	and SCO.COM_KEY = #{comKey}


	and SCO.PARTNER_KEY = #{partnerKey}


	and SCO.PARTNER_NAME LIKE '%${partnerName}%'


	and SCO.ORDER_DATE BETWEEN #{startDate} AND #{endDate}


	and SCO.IS_URGENT = #{isUrgent}


	and SCO.ORDER_STATUS = #{orderStatus}


	and SCO.INVOICE_STATUS = #{invoiceStatus}


	and SCO.INDUSTRY_ID = #{industryId}


	and SCO.SALES_NO = #{salesNo}


	and SCO.PURCHASECHECKING_NO = #{purchasecheckingNo}

order by SCO.UID

    select DISTINCT
    ,CONCAT(SO.SALES_ORDER_NUM,' (',SO.DETAIL_NUM,')') orderCountValue
    from YDZ_SALESCHECKING_ORDER SO
    LEFT JOIN YDZ_RECEIPT_PAYMENT RP on SO.SALESCHECKING_NO = RP.CHECKING_NO
    LEFT JOIN YDZ_CHECKING_INVOICE CI on SO.SALESCHECKING_NO = CI.CHECKING_NO
    where SO.IS_VALID = '1' AND SO.IS_DEL = '0' AND SO.SALESCHECKING_NO IN (
    	SELECT DISTINCT
    		s.SALESCHECKING_NO
    	FROM YDZ_SALESCHECKING_ORDER s,YDZ_SALESCHECKING_ORDER_DETAIL d
    	where s.IS_VALID = '1' AND d.IS_DEL = '0' AND d.IS_VALID = '1' AND d.IS_DEL = '0'
    	AND s.SALESCHECKING_NO = d.SALESCHECKING_NO
    	and s.COM_KEY = #{pd.comKey}

	    	and s.PARTNER_KEY = #{pd.partnerKey}


	    	and s.PARTNER_NAME LIKE '%${pd.partnerName}%'


	    	and s.CONTACT_ID = #{pd.contacterId}


	    	and s.CREATE_TIME >= #{pd.startDate}


	    	and s.CREATE_TIME <= #{pd.endDate}


	    	AND CONCAT(s.SALESCHECKING_NO,s.CASE_DESC,d.BUSINESS_DESC) LIKE '%${pd.searchKey}%'


	    	and s.ORDER_STATUS = #{pd.orderStatus}


	        and s.INVOICE_STATUS = #{pd.invoiceStatus}


	    	and s.INDUSTRY_ID = #{pd.industryId}


	    	and s.SALES_NO = #{pd.salesNo}


	    	and s.PURCHASECHECKING_NO = #{pd.purchasecheckingNo}

    )

    	order by SO.UID DESC


    	order by RP.RECPAY_DATE DESC,SO.CREATE_TIME DESC

select

from YDZ_SALESCHECKING_ORDER SO
LEFT JOIN YDZ_RECEIPT_PAYMENT RP on SO.SALESCHECKING_NO = RP.CHECKING_NO
LEFT JOIN YDZ_CHECKING_INVOICE CI on SO.SALESCHECKING_NO = CI.CHECKING_NO
where SO.IS_VALID = '1' AND SO.IS_DEL = '0' and SO.COM_KEY = #{pd.comKey}

  and SO.SALESCHECKING_NO = #{pd.salescheckingNo}


  and SO.PARTNER_KEY = #{pd.partnerKey}


  and SO.PARTNER_NAME LIKE '%${pd.partnerName}%'


  and SO.ORDER_DATE BETWEEN #{pd.startDate} AND #{pd.endDate}


  and SO.IS_URGENT = #{pd.isUrgent}


  and SO.ORDER_STATUS = #{pd.orderStatus}


  and SO.ORDER_STATUS IN ${pd.orderStatusIN}


  and SO.INVOICE_STATUS = #{pd.invoiceStatus}


  and SO.INDUSTRY_ID = #{pd.industryId}


  and SO.SALES_NO = #{pd.salesNo}


  and SO.PURCHASECHECKING_NO = #{pd.purchasecheckingNo}

order by SO.UID desc

