mapper.select:
select 
,u.REAL_NAME creatorName
from ydz_installation_order io, ydz_user u
where io.IS_VALID = '1' AND io.IS_DEL = '0' AND io.COM_KEY = #{comKey}
and io.CREATOR = u.USER_KEY

	and io.INDUSTRY_ID = #{industryId}


	and io.INSTALLATION_NO = #{installationNo}

select 

from ydz_installation_order
where IS_VALID = '1' AND IS_DEL = '0' AND COM_KEY = #{pd.comKey}

	and INDUSTRY_ID = #{pd.industryId}


	and PARTNER_KEY = #{pd.partnerKey}


	and CASE_DESC LIKE '%${pd.caseDesc}%'


	and ORDER_DATE BETWEEN #{pd.startDate} AND #{pd.endDate}


	and STATUS = #{pd.partnerKey}


	and INSTALLATION_NO IN ${pd.installationNoIns}

order by UID desc

select 

from ydz_installation_order
where IS_VALID = '1' AND IS_DEL = '0' AND COM_KEY = #{comKey}

	and INSTALLATION_NO IN ${installationNoIns}

order by ORDER_DATE ASC

select 
sum(DISCOUNT) sumDiscount,sum(PREFERENTIAL) sumPreferential,sum(PREPAY_PRICE) sumPrepayPrice,sum(PAYABLE_PRICE) sumPayablePrice, sum(TOTAL_PRICE) sumTotalPrice, sum(INSTALLATION_PRICE) sumInstallationPrice,CONCAT(MIN(ORDER_DATE),' è‡³ ',MAX(ORDER_DATE)) orderCycle
from ydz_installation_order
where IS_VALID = '1' AND IS_DEL = '0' AND COM_KEY = #{comKey}

	and INSTALLATION_NO IN ${installationNoIns}

SELECT d.UID, d.SALES_NO, d.ITEM_NO, d.BUSINESS_TYPE, d.BUSINESS_DESC, d.REQUIREMENTS, d.SPECIFICATION, d.PRICE_WAY, d.LENGTH, d.WIDTH, d.QUANTITY, d.UNIT , d.UNIT_PRICE , d.PREFERENTIAL , d.REMARKS , d.IS_VALID , d.IS_DEL , d.ORDER_STATUS , d.TOTAL_PRICE , s.COM_KEY , s.PARTNER_KEY , s.PARTNER_NAME, s.PARTNER_SHORTNAME, s.ORDER_STATUS ORDER_STATUS_MAIN, s.INDUSTRY_ID, s.ORDER_DATE , s.CONTACT_ID, s.CONTACT_NAME, s.CREATE_TIME, s.TOTAL_PRICE totalPriceOrder, s.PURCHASE_NO purchaseNo, s.SALESCHECKING_NO salescheckingNo, s.CASE_DESC caseDesc FROM YDZ_SALES_ORDER_DETAIL d , YDZ_SALES_ORDER s where d.SALES_NO = s.SALES_NO and d.IS_VALID = '1' and d.IS_DEL = '0' and s.COM_KEY = #{pd.comKey}
and s.PARTNER_KEY = #{pd.partnerKey}
and s.ORDER_DATE BETWEEN #{pd.startDate} AND #{pd.endDate}
and CONCAT(d.SALES_NO,d.BUSINESS_DESC,s.CASE_DESC) LIKE '%${pd.searchKey}%'
and d.UID in ${pd.detailUid}
order by s.ORDER_DATE DESC,s.CREATE_TIME DESC

mapper.delete:
delete from ydz_installation_order
where INSTALLATION_NO = #{installationNo}

	and COM_KEY = #{comKey}

mapper.update:
	update ydz_installation_order
  set STATUS = #{status}
  where COM_KEY = #{comKey}

  	and INSTALLATION_NO = #{installationNo}


  	and INSTALLATION_NO IN ${installationNoIns}
