mapper.select:
select 

from YDZ_ROLE_USER
where COM_KEY = #{comKey}

	and ROLE_KEY = #{roleKey}


	and USER_KEY = #{userKey}

mapper.insert:
    insert into YDZ_ROLE_USER (ROLE_KEY, USER_KEY, 
      COM_KEY, INDUSTRY_ID, CREATOR, CREATE_TIME
      )
    values 

	    (#{item.roleKey,jdbcType=VARCHAR}, #{item.userKey,jdbcType=VARCHAR}, 
	      #{item.comKey,jdbcType=VARCHAR}, #{item.industryId,jdbcType=VARCHAR}, 
	      #{item.creator,jdbcType=VARCHAR}, #{item.createTime,jdbcType=TIMESTAMP}
	      )
